import React from "react";

class Clickimg extends React.Component {

  state = {
    item: {
      src:"https://cdn-02.independent.ie/regionals/kerryman/news/article34250758.ece/AUTOCROP/w620/2015-12-02_ker_15039481_I1.JPG",
      score: 0, 
    },
    item2: {
      src:"https://www.nydailynews.com/resizer/3vON2MK4UseCi-KcXQxivVXOPxk=/800x0/arc-anglerfish-arc2-prod-tronc.s3.amazonaws.com/public/4FLF6TGOFJSOHINHYGZABAQBFM.jpg",
      score: 0, 
    },
    item3: {
      src:"http://www.followingthenerd.com/site/wp-content/uploads/mark-hamill-luke-skywalker.jpeg",
      score: 0, 
    },
    item4: {
