      score: 0, 
    },
    item5: {
      src:"https://vignette.wikia.nocookie.net/headhuntersholosuite/images/4/46/BB-8.png/revision/latest?cb=20160117212745",
      score: 0, 
    },
    item6: {
      src:"https://cdn.gamerant.com/wp-content/uploads/Star-Wars-Battlefront-Palpatine.jpg.optimal.jpg",
      score: 0, 
    },
    item7: {
      src:"https://i.pinimg.com/originals/37/f7/92/37f79245814b336bd27518b94e081b61.jpg",
      score: 0, 
    },
    item8: {
      src:"https://vignette.wikia.nocookie.net/p__/images/7/74/Star-wars-obi-wan-kenobi-jedi-cloak-3.jpg/revision/latest?cb=20171231024120&path-prefix=protagonist",
      score: 0, 
    },
    item9: {
      src:"https://i.pinimg.com/originals/9b/03/d7/9b03d7aeac67b0285bceeed283fa2e7a.png",
      score: 0, 
    },
    item10: {
      src:"https://hips.hearstapps.com/digitalspyuk.cdnds.net/17/44/1509643536-qui-gon-jinn-star-wars-the-phantom-menace.jpg",
      score: 0, 
    },
    item11: {
      src:"https://starwarsblog.starwars.com/wp-content/uploads/2015/05/CC305017L3-1536x864-641515734419.jpg",
      score: 0, 
    },
    item12: {
      src:"https://amp.businessinsider.com/images/529e230ceab8eaca7369ae60-750-563.jpg",
      score: 0, 
    },
  }

  getValue=()=>{
    this.state.item.score += 1;
  }

  render() {
    console.log(this.state)
    return (
      <div>
        <img onClick={this.getValue} className="card-image" src={this.state.item.src} alt="star-wars" value={this.state.item.score}></img>
        <img className="card-image" src={this.state.item2.src} alt="star-wars" value={this.state.item2.score}></img>
        <img className="card-image" src={this.state.item3.src} alt="star-wars" value={this.state.item3.score}></img>
        <img className="card-image" src={this.state.item4.src} alt="star-wars" value={this.state.item4.score}></img>
        <img className="card-image" src={this.state.item5.src} alt="star-wars" value={this.state.item5.score}></img>
        <img className="card-image" src={this.state.item6.src} alt="star-wars" value={this.state.item6.score}></img>
        <img className="card-image" src={this.state.item7.src} alt="star-wars" value={this.state.item7.score}></img>
        <img className="card-image" src={this.state.item8.src} alt="star-wars" value={this.state.item8.score}></img>
        <img className="card-image" src={this.state.item9.src} alt="star-wars" value={this.state.item9.score}></img>
        <img className="card-image" src={this.state.item10.src} alt="star-wars" value={this.state.item10.score}></img>
        <img className="card-image" src={this.state.item11.src} alt="star-wars" value={this.state.item11.score}></img>
        <img className="card-image" src={this.state.item12.src} alt="star-wars" value={this.state.item12.score}></img>
      </div>
    );
  }

}

export default Clickimg;
